package 第４章;

public class Sample4_1_2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1 = 2;
		int num2 = 3;
		int sum = num1 + num2;
		System.out.println("変数num1の値は" + num1 + "です。");
		System.out.println("変数num2の値は" + num2 + "です。");
		System.out.println("変数sumの値は" + sum + "です。");
		
		num1 = num1 + 1;
		System.out.println("変数num1の値に１をたすと" + num1 + "です。");
	}

}
