package 第４章;

public class Sample4_2_1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1 =10;
		int num2 = 5;
		
		System.out.println("num1とnum2にいろいろ演算を行います。");
		System.out.println("num1+num2は" + (num1+num2) + "です。");
		System.out.println("num1-num2は" + (num1-num2) + "です。");
		System.out.println("num1*num2は" + (num1*num2) + "です。");
		System.out.println("num1/num2は" + (num1/num2) + "です。");
		System.out.println("num1%num2は" + (num1%num2) + "です。");
	}

}
