package 第７章;

public class Sample7_6_1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int test[] = {80,70,60,50,40};
		
		for(int i=0;i<5;i++){
			System.out.println((i+1) + "番目の人の点数は" + test[i] + "です。");
		}
		System.out.println("テストの受験者は" + test.length + "人です。");
		
	}

}
